def on_up_pressed():
    global cnt
    if hacker.vy == 0:
        hacker.vy = -120
        cnt = 1
    elif cnt == 1:
        hacker.vy = -120
        cnt += 2
    if hacker.vx < 0:
        animation.run_image_animation(hacker,
            assets.animation("""
                left jump animation
            """),
            300,
            False)
    else:
        animation.run_image_animation(hacker,
            assets.animation("""
                right jump animation
            """),
            300,
            False)
controller.up.on_event(ControllerButtonEvent.PRESSED, on_up_pressed)

